sp_widget.script:
(function() {

	if (input) {
		data.table = input.table;
		data.sys_id = input.sys_id;
		data.view = input.view;
	} else {
		data.table = options.table || $sp.getParameter('table');
		data.sys_id = options.sys_id || $sp.getParameter('sys_id') || '-1';
		data.view = options.view || $sp.getParameter('view') || 'service_portal';
	}

	if (!data.table) {
		gs.addErrorMessage('<strong>Error:</strong> there was no table name supplied for this form');
	}

	var $fd = new FormDecoratorAPI($sp);
	data.form = $fd.getDecoratedForm(data.table, data.sys_id, null, data.view, options.primary_only);

})();
