sys_ui_script.script:
angular.module('formDec')

.directive('fdExampleIntegerField', function ($timeout) {
	'use strict';

	var template = '<div class="fd-ex-integer" ng-class="{\'fd-ex-mandatory\': field.mandatory}"><label class="fd-ex-label" for="{{::field.name}}">{{::field.label}}</label><input type="text" name="{{::field.name}}" ng-model="field.stagedValue"/></div>';

	var link = function (scope, elem, attrs, ctrl) {

		scope.inputField = elem.find('input');
		scope.inputField.on('focus', scope.focusHandler).on('blur', scope.blurHandler);

		$timeout(function () {
			scope.$emit("sp.spFormField.rendered", elem, scope.inputField);
		});

	};

	return {
		template: template,
		link: link,
		controller: 'fdFieldCtrl',
		require: ['^spModel', '^fdFormField']
	};
})


.directive('fdExampleCheckboxField', function ($timeout) {
	'use strict';

	var template = '<div class="fd-ex-checkbox" ng-class="{\'fd-ex-mandatory\': field.mandatory}"><label class="fd-ex-label" for="{{::field.name}}">{{::field.label}}</label><input type="checkbox" name="{{::field.name}}"/></div>';
	var link = function (scope, elem, attrs, ctrl) {};

	return {
		template: template,
		link: link,
		require: ['^spModel', '^fdFormField']
	};
})


.directive('fdExampleStringField', function ($timeout) {
	'use strict';

	var template = '<div class="fd-ex-string" ng-class="{\'fd-ex-mandatory\': field.mandatory}"><label class="fd-ex-label" for="{{::field.name}}">{{::field.label}}</label><input type="text" name="{{::field.name}}" ng-model="field.stagedValue"/></div>';
	var link = function (scope, elem, attrs, ctrl) {

		scope.inputField = elem.find('input');
		scope.inputField.on('focus', scope.focusHandler).on('blur', scope.blurHandler);

		$timeout(function () {
			scope.$emit("sp.spFormField.rendered", elem, scope.inputField);
		});

	};

	return {
		template: template,
		link: link,
		controller: 'fdFieldCtrl',
		require: ['^spModel', '^fdFormField']
	};
})


.directive('fdExampleYesNoField', function ($timeout) {
	'use strict';

	var template = '<div class="fd-ex-yn" ng-class="{\'fd-ex-mandatory\': field.mandatory}"><div class="fd-ex-label">{{::field.label}}</div><input id="{{::field.name}}_yes" type="radio" name="{{::field.name}}" value="true" name="radio" ng-click="setYes()" /><label for="{{::field.name}}_yes" >Yes</label><input id="{{::field.name}}_no" type="radio" name="{{::field.name}}" value="true" name="radio" ng-click="setNo()"/><label for="{{::field.name}}_no" >No</label></div>';
	var link = function (scope, elem, attrs, ctrl) {

		scope.setYes = function () {
			scope.field.stagedValue = 'true';
			scope.blurHandler();
		};

		scope.setNo = function () {
			scope.field.stagedValue = 'false';
			scope.blurHandler();
		};
	};

	return {
		template: template,
		link: link,
		controller: 'fdFieldCtrl',
		require: ['^spModel', '^fdFormField']
	};
});
