sys_ui_script.script:
angular.module('formDec')

.directive('fdExampleIntegerField', function ($timeout) {
	'use strict';

	var template = '<div class="fd-ex-integer" ng-class="{\'fd-ex-mandatory\': field.mandatory}"><label class="fd-ex-label" for="{{::field.name}}">{{::field.label}}</label><input type="number" name="{{::field.name}}" ng-model="field.stagedValue"/></div>';

	var link = function (scope, elem, attrs) {

		scope.field.stagedValue = parseInt(scope.field.stagedValue);

		scope.inputField = elem.find('input');
		scope.inputField.on('focus', scope.focusHandler).on('blur', scope.blurHandler);

		$timeout(function () {
			scope.$emit("sp.spFormField.rendered", elem, scope.inputField);
		});

	};

	return {
		template: template,
		link: link,
		controller: 'fdFieldCtrl'
	};
})


.directive('fdExampleCheckboxField', function ($timeout) {
	'use strict';

	var template = '<div class="fd-ex-checkbox" ng-class="{\'fd-ex-mandatory\': field.mandatory}" ng-show="field.visible"><div class="fd-ex-label">&nbsp;</div><div class="row"><div class="col-xs-12"><input type="checkbox" ng-attr-id="{{::\'fd_\' + field.name}}" ng-model="field.stagedValue" ng-true-value="\'true\'" ng-false-value="\'false\'" ng-change="setField()"/><label ng-attr-for="{{::\'fd_\' + field.name}}" class="fd-ex-label"><span></span>{{::field.label}}</label></div></div></div>';
	var link = function (scope, elem, attrs) {

		scope.setField = function () {
			scope.blurHandler();
		};

	};

	return {
		template: template,
		link: link,
		controller: 'fdFieldCtrl'
	};
})


.directive('fdExampleStringField', function ($timeout) {
	'use strict';

	var template = '<div class="fd-ex-string" ng-class="{\'fd-ex-mandatory\': field.mandatory}" ng-show="field.visible"><label class="fd-ex-label" for="{{::field.name}}">{{::field.label}}</label><input type="text" name="{{::field.name}}" ng-model="field.stagedValue"/></div>';
	var link = function (scope, elem, attrs) {

		scope.inputField = elem.find('input');
		scope.inputField.on('focus', scope.focusHandler).on('blur', scope.blurHandler);

		$timeout(function () {
			scope.$emit("sp.spFormField.rendered", elem, scope.inputField);
		});

	};

	return {
		template: template,
		link: link,
		controller: 'fdFieldCtrl'
	};
})


.directive('fdExampleYesNoField', function ($timeout) {
	'use strict';

	var template = '<div class="fd-ex-yn" ng-class="{\'fd-ex-mandatory\': field.mandatory}" ng-show="field.visible"><div class="fd-ex-label">{{::field.label}}</div><div class="row"><div class="col-xs-6"><button ng-click="setYes()" ng-class="{\'fd-ex-yn-active\': fieldVal}">Yes</button></div><div class="col-xs-6"><button ng-click="setNo()" ng-class="{\'fd-ex-yn-active\': !fieldVal}">No</button></div></div></div>';
	var link = function (scope, elem, attrs) {

		scope.fieldVal = scope.field.stagedValue == 'true';

		scope.setYes = function () {
			scope.fieldVal = true;
			scope.field.stagedValue = 'true';
			scope.blurHandler();
		};

		scope.setNo = function () {
			scope.fieldVal = false;
			scope.field.stagedValue = 'false';
			scope.blurHandler();
		};

		$timeout(function () {
			scope.$emit("sp.spFormField.rendered", elem, scope.inputField);
		});

	};

	return {
		template: template,
		link: link,
		controller: 'fdFieldCtrl'
	};
});
